mapper.select:
    
		  select * from 
			 (
			    select c.*,rownum rnum
						   from(
		                          select u.ui_name,u.ui_id,u.ui_email,u.ui_phone from user_information u,user_addr a where u.ui_id=a.ui_id and u.ui_id=#{ui_id}
	                  )c
              )
         where rnum=1

select * from 
(
	select a.*,rownum rnum
	 from(
	 	
	 	
	 	 order by o.or_regdate desc, o.or_status desc
	 )a
)
where rnum>=${startRow} and rnum<=${endRow}

mapper.sql:
select * from ( select a.*,rownum rnum1 from( select o.or_request,o.or_regdate,o.or_deltime,o.or_status,o.or_totalcost,o.or_addr, o.or_paymethod,f.food_name,f.food_cost,r.r_name,o.or_num,r.r_img from orders o,food f,orders_detail d,orders_option p,restaurant r where o.or_num=d.or_num and d.od_num=p.od_num(+) and f.r_id=r.r_id and f.food_num=d.food_num and o.or_num=#{or_num}
order by o.or_regdate desc )a
)b where rnum1=1

